    public int length;
    public int lengthOfknown = 1;
    private int setLength = 1;
    public bool changeWord = false;
    public int getWord = 0;
    // Start is called once before the first execution of Update after the MonoBehaviour is created
    void Start()
    {
        length = phrases.Count;
        // Исправлено: ограничиваем lengthOfknown количеством пар фраз
        //lengthOfknown = Mathf.Min(setLength, length / 2); Now using initialPhrases
        Debug.Log($"Phrases initialized: {length} elements, {length/2} pairs, lengthOfknown: {lengthOfknown}");
    }

    void Update(){}

    public List<List<string>> GetAllPhrases()
    {
        List<List<string>> allPhrases = new List<List<string>>();
        
        if (phrases.Count % 2 != 0)
        {
            Debug.LogError($"Phrases count ({phrases.Count}) is not even! Should be pairs.");
            return allPhrases;
        }
        
        for (int i = 0; i < phrases.Count; i += 2)
        {
            List<string> phrasePair = new List<string>
            {
                phrases[i],
                phrases[i + 1]
            };
            allPhrases.Add(phrasePair);
        }
        
        Debug.Log($"Loaded {allPhrases.Count} phrase pairs");
        return allPhrases;
    }

    public (List<string> russian, List<string> english) GetPhraseLists()
    {
        List<string> russian = new List<string>();
        List<string> english = new List<string>();
        
        for (int i = 0; i < phrases.Count; i += 2)
        {
            if (i < phrases.Count) russian.Add(phrases[i]);
            if (i + 1 < phrases.Count) english.Add(phrases[i + 1]);
        }
        
        return (russian, english);
    }

    public int GetPhraseCount()
    {
        return phrases.Count / 2;
    }
    
    public List<string> GetPhrase()
    {
        /*return new List<string> { 
                phrases[0], 
                phrases[1], 
                "Yes"
            };*/
        int pairsCount = phrases.Count / 2;
        
        // Всегда ограничиваем lengthOfknown количеством пар
        if (lengthOfknown > pairsCount)
        {
            lengthOfknown = pairsCount;
        }
        
        if (lengthOfknown > setLength || changeWord)
        {
            string ifNew = "No";
            
            if (changeWord)
            {
                changeWord = false;
                // Увеличиваем количество известных пар, но не больше общего количества
                if (lengthOfknown < pairsCount)
                {
                    ++lengthOfknown;
                }
            }
            
            int choosePairIndex;
            if (Random.Range(0, 10) >= 7)
            {
                // Берем новую пару (последнюю из известных)
                choosePairIndex = lengthOfknown - 1;
                //Debug.Log($"GetPhrase: New phrase chosen at index {phrases[choosePairIndex]}");
                ifNew = "Yes";
            }
            else
            {
                // Берем случайную пару из уже известных
                choosePairIndex = Random.Range(0, lengthOfknown);
            }
            
            // Проверяем границы
            if (choosePairIndex >= pairsCount) choosePairIndex = pairsCount - 1;
            
            int stringIndex = choosePairIndex * 2;
            
            //Debug.Log($"GetPhrase: pairIndex={choosePairIndex}, stringIndex={stringIndex}, phrase={phrases[stringIndex]}");
            
            return new List<string> { 
                phrases[stringIndex], 
                phrases[stringIndex + 1], 
                ifNew 
            };
        }
        else
        {
            // Берем случайную пару из известных
            int choosePairIndex = Random.Range(0, lengthOfknown);
            
            // Проверяем границы
            if (choosePairIndex < 0) choosePairIndex = 0;
            if (choosePairIndex >= pairsCount) choosePairIndex = pairsCount - 1;
            
            int stringIndex = choosePairIndex * 2;
            
            //Debug.Log($"GetPhrase (else): pairIndex={choosePairIndex}, stringIndex={stringIndex}, phrase={phrases[stringIndex]}");
            
            return new List<string> { 
                phrases[stringIndex], 
                phrases[stringIndex + 1], 
                "Yes"
            };
        }
    }
    
    // Добавим вспомогательный метод для отладки
    public string GetDebugInfo()
    {
        return $"Total elements: {phrases.Count}, Pairs: {phrases.Count/2}, lengthOfknown: {lengthOfknown}";
    }
}